// define counter action constants here

export const RESET_VALUE = "Reset Value";
export const DECREMENT_VALUE = "Dcrement Value";
export const INCREMENT_VALUE = "Increment Value";
// define counter action creators here

export const reset = () => ({ type: RESET_VALUE });
export const decrement = () => ({ type: DECREMENT_VALUE });
export const increment = () => ({ type: INCREMENT_VALUE });
